settings.cellContentFunction:
var deviceType = value;
var color = '#fff';
switch (deviceType) {
  case 'online':
    color = '#E9F1EB';
    break;
  case 'offline':
    color = '#F0DEDE';
    break;
}
return '<div style="border-radius: 10px; padding: 5px; ' +
  'background-color: '+ color +'; ' +
  'text-align: center;">' + deviceType + '</div>';
